INSERT INTO `scenicspots_spots` VALUES (2,'国家速滑馆','<p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">国家速滑馆（</span>“<span style=\"font-family:宋体\">冰丝带</span><span style=\"font-family:Calibri\">”</span><span style=\"font-family:宋体\">），位于北京市朝阳区近奥林匹克公园林萃路</span><span style=\"font-family:Calibri\">2</span><span style=\"font-family:宋体\">号，是</span><span style=\"font-family:Calibri\">2022</span><span style=\"font-family:宋体\">年北京冬奥会北京主赛区标志性场馆、唯一新建的冰上竞赛场馆。</span><span style=\"font-family:Calibri\">2019</span><span style=\"font-family:宋体\">年年底，国家速滑馆项目基本完成。</span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\">“<span style=\"font-family:宋体\">冰丝带</span><span style=\"font-family:Calibri\">”</span><span style=\"font-family:宋体\">的设计理念来自一个冰和速度结合的创意，</span><span style=\"font-family:Calibri\">22</span><span style=\"font-family:宋体\">条丝带就像运动员滑过的痕迹，象征速度和激情。国家速滑馆将作为北京冬奥会大道速滑比赛场地。拥有亚洲最大的全冰面设计，冰面面积达</span><span style=\"font-family:Calibri\">1.2</span><span style=\"font-family:宋体\">万平方米。平时可接待超过</span><span style=\"font-family:Calibri\">2000</span><span style=\"font-family:宋体\">人同时开展冰球、速度滑冰、花样滑冰、冰壶等所有冰上运动。</span></span></p><p><span style=\";font-family:Calibri;font-size:14px\">&nbsp;</span></p><p><span style=\";font-family:Calibri;font-size:14px\">&nbsp;<img src=\"/media/Scenic/ueditor/速滑馆3_20220102164538_581.png\" title=\"\" alt=\"速滑馆3.png\"/></span></p><p><span style=\";font-family:Calibri;font-size:14px\">&nbsp;</span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">国家速滑馆</span>“<span style=\"font-family:宋体\">冰丝带</span><span style=\"font-family:Calibri\">”</span><span style=\"font-family:宋体\">采用世界跨度最大的单层双向正交马鞍形索网屋面，用钢量仅为传统屋面的四分之一。 </span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">国家速滑馆是世界上首个采用二氧化碳跨临界直冷制冰技术的冬奥速滑场馆。该技术是世界上比较先进环保的制冰技术，碳排放值趋近于零，制冰能效大幅提升。</span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">赛后冰面可进行分区制冷，对每块冰单独控温，实现场馆</span>“<span style=\"font-family:宋体\">同时运行、不同使用</span><span style=\"font-family:Calibri\">”</span><span style=\"font-family:宋体\">。场馆制冰系统集成设计和冰板结构设计两个关键设计方案已取得专利。</span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">为打造</span>“<span style=\"font-family:宋体\">最快的冰</span><span style=\"font-family:Calibri\">”</span><span style=\"font-family:宋体\">，国家速滑馆冰板层施工精益求精，用时</span><span style=\"font-family:Calibri\">60</span><span style=\"font-family:宋体\">多天完成制冰钢管</span><span style=\"font-family:Calibri\">8574</span><span style=\"font-family:宋体\">道焊口焊接，制出的混凝土冰板层水平高差在</span><span style=\"font-family:Calibri\">4</span><span style=\"font-family:宋体\">毫米左右，非常平整；冰面温差被控制在</span><span style=\"font-family:Calibri\">0.5</span><span style=\"font-family:宋体\">摄氏度内，利于运动员创造佳绩。</span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">国家速滑馆场地照明系统根据场馆功能量身打造，由</span>1088<span style=\"font-family:宋体\">套</span><span style=\"font-family:Calibri\">LED</span><span style=\"font-family:宋体\">灯具组成，其光学参数满足顶级赛事举办和高清转播要求，节能环保性能突出。每套灯具均实现智能独立控制，满足场馆赛时和赛后多功能使用的丰富照明需求。</span></span></p><p></p><p><img src=\"/media/Scenic/ueditor/速滑馆4_20220102164546_249.png\" title=\"\" alt=\"速滑馆4.png\"/></p>','spots/thumbnail/2022/01/速滑馆1.png','spots/mainfigure/2022/01/速滑馆2.png','natural','000111111','全年','北京市朝阳区近奥林匹克公园林萃路2号',100,112.460033,34.624376,'2022-01-02 16:39:00.000000'),(3,'国家体育场','<p style=\"text-indent: 28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">国家体育场（鸟巢），位于北京奥林匹克公园中心区南部，为</span>2008<span style=\"font-family:宋体\">年北京奥运会的主体育场，占地</span><span style=\"font-family:Calibri\">20.4</span><span style=\"font-family:宋体\">公顷，建筑面积</span><span style=\"font-family:Calibri\">25.8</span><span style=\"font-family:宋体\">万平方米，可容纳观众</span><span style=\"font-family:Calibri\">9.1</span><span style=\"font-family:宋体\">万人。举行了奥运会、残奥会开闭幕式、田径比赛及足球比赛决赛。奥运会后成为北京市民参与体育活动及享受体育娱乐的大型专业场所，并成为地标性的体育建筑和奥运遗产。</span></span></p><p style=\"text-indent: 28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\"><img src=\"/media/Scenic/ueditor/国家体育场1_20220102164831_810.jpg\" title=\"\" alt=\"国家体育场1.jpg\"/></span></span></p><p style=\"text-indent: 28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">整个体育场结构的组件相互支撑，形成网格状的构架，外观看上去就仿若树枝织成的鸟巢，其灰色矿质般的钢网以透明的膜材料覆盖，其中包含着一个土红色的碗状体育场看台。在这里，中国传统文化中镂空的手法、陶瓷的纹路、红色的灿烂与热烈，与现代最先进的钢结构设计完美地相融在一起。</span></span></p><p style=\"text-indent: 28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">整个建筑通过迪士尼巨型网状结构联系，内部没有一根立柱，看台是一个完整的没有任何遮挡的碗状造型，如同一个巨大的容器，赋予体育场以不可思议的戏剧性和无与伦比的震撼力。</span></span></p><p><br/></p>','spots/thumbnail/2022/01/国家体育场.jpg','spots/mainfigure/2022/01/国家体育场.jpg','natural','01111111','全年','北京市朝阳区国家体育场南路1号',100,112.460033,34.624376,'2022-01-02 16:46:00.000000'),(4,'国家游泳中心','<p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">国家游泳中心（</span>National Aquatics center<span style=\"font-family:宋体\">），别名</span><span style=\"font-family:Calibri\">“</span><span style=\"font-family:宋体\">水立方</span><span style=\"font-family:Calibri\">”</span><span style=\"font-family:宋体\">、</span><span style=\"font-family:Calibri\">“</span><span style=\"font-family:宋体\">冰立方</span><span style=\"font-family:Calibri\">”</span><span style=\"font-family:宋体\">，位于北京市朝阳区北京奥林匹克公园内</span></span><span style=\";font-family:宋体;font-size:14px\">。</span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">国家游泳中心总建筑面积</span>65000—80000<span style=\"font-family:宋体\">平方米，其中地下部分的建筑面积不少于</span><span style=\"font-family:Calibri\">15000</span><span style=\"font-family:宋体\">平方米，长宽高分别为</span><span style=\"font-family:Calibri\">177</span><span style=\"font-family:宋体\">米</span><span style=\"font-family:Calibri\">×177</span><span style=\"font-family:宋体\">米</span><span style=\"font-family:Calibri\">×30</span><span style=\"font-family:宋体\">米。场馆外观如同一个冰晶状的立方体，造型简洁现代。场馆内部是一个六层楼建筑，平面呈正方形。馆内设施主要包括比赛大厅、热身池，多功能大厅以及大型嬉水乐园。</span><span style=\"font-family:Calibri\">“</span><span style=\"font-family:宋体\">冰立方</span><span style=\"font-family:Calibri\">”</span><span style=\"font-family:宋体\">冰上运动中心位于国家游泳中心南广场地下空间，整体建筑面积约</span><span style=\"font-family:Calibri\">8000</span><span style=\"font-family:宋体\">平方米，由一块</span><span style=\"font-family:Calibri\">1830</span><span style=\"font-family:宋体\">平方米的标准冰场和一块由四条</span><span style=\"font-family:Calibri\">45</span><span style=\"font-family:宋体\">米</span><span style=\"font-family:Calibri\">×5</span><span style=\"font-family:宋体\">米的标准冰壶场地及配套服务设施组成。 </span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\">&nbsp;<img src=\"/media/Scenic/ueditor/国家游泳中心_20220102165151_512.jpg\" title=\"\" alt=\"国家游泳中心.jpg\"/></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">国家游泳中心为具有国际先进水平的、集游泳、运动、健身、休闲于一体的中心，将成为北京市民的水上娱乐中心。国家游泳中心利用南广场地下空间建立了</span>2<span style=\"font-family:宋体\">块冰面，一块为标准冰场、另一块为冰壶场地，将作为奥林匹克中心区冰壶项目体验基地，为大众提供开放的平台，为三亿人参与冰雪运动提供助力。</span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\">&nbsp;</span></p><p><br/></p>','spots/thumbnail/2022/01/游泳中心1.png','spots/mainfigure/2022/01/国家游泳中心.jpg','natural','0000000000','全年','北京市朝阳区天辰东路11号',100,112.460033,34.624376,'2022-01-02 16:50:00.000000'),(5,'首都体育馆','<p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">首都体育馆修建于</span>1968<span style=\"font-family:宋体\">年，建筑面积约</span><span style=\"font-family:Calibri\">5.3</span><span style=\"font-family:宋体\">万平方米，属于综合性、多功能的体育馆，是北京重要的体育文化活动场所之一。在建成后的</span><span style=\"font-family:Calibri\">38</span><span style=\"font-family:宋体\">年时间里，承担了众多的国际、国内体育比赛和训练，以及大型文艺演出任务。为满足</span><span style=\"font-family:Calibri\">2008</span><span style=\"font-family:宋体\">年奥运会排球比赛和训练的需要，首都体育馆进行大规模改建工程。 首都体育馆场馆改造完成后建筑面积增至约</span><span style=\"font-family:Calibri\">5.46</span><span style=\"font-family:宋体\">万平方米，满足国际奥委会及国际单项体育组织的功能需求。改造工程主要包括比赛馆、综合训练馆和赛时附属用房，扩建部分主要是售票大厅和四个物品寄存处，另外还要对馆区环境进行改造和修缮。改建后的首都体育馆不仅可以满足</span><span style=\"font-family:Calibri\">2008</span><span style=\"font-family:宋体\">年奥运会排球项目的训练和比赛，还能满足赛后其它大型活动的要求。</span></span></p><p><span style=\";font-family:Calibri;font-size:14px\">&nbsp;<img src=\"/media/Scenic/ueditor/首都1_20220102165408_817.png\" title=\"\" alt=\"首都1.png\"/></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">首都体育馆是北京规模最大、功能最多、适用范围最广的体育馆。东邻北京动物园，西望紫竹院公园和中国国家图书馆，绿荫环抱，空间广阔，环境优美。</span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">首都体育馆是上个世纪</span>60<span style=\"font-family:宋体\">年代建筑，此次奥运工程改扩建给首都体育馆增添了新的生命力，在比赛场地东西两侧增加</span><span style=\"font-family:Calibri\">12</span><span style=\"font-family:宋体\">个观众包厢，在馆外东西两侧各设两部电梯，还改善了通风空调、广播音响等设施，可更好地为观众服务。</span></span></p><p><br/></p>','spots/thumbnail/2022/01/首都1.png','spots/mainfigure/2022/01/首都体育馆.jpg','natural','00000000','全年','北京市海淀区中关村南大街56号(近地铁四号线)',80,112.460033,34.624376,'2022-01-02 16:52:00.000000'),(6,'首钢滑雪大跳台','<p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">首钢滑雪大跳台由赛道、裁判塔和看台区域三部分组成，赛道长</span>164<span style=\"font-family:宋体\">米、最宽处</span><span style=\"font-family:Calibri\">34</span><span style=\"font-family:宋体\">米、最高点</span><span style=\"font-family:Calibri\">60</span><span style=\"font-family:宋体\">米。选手从大跳台滑下后，将完成空翻、回转等技术动作。北京冬奥会时，首钢滑雪大跳台将产生</span><span style=\"font-family:Calibri\">4</span><span style=\"font-family:宋体\">块金牌。</span></span></p><p><img src=\"/media/Scenic/ueditor/首钢1_20220102165627_213.png\" title=\"\" alt=\"首钢1.png\"/><span style=\";font-family:Calibri;font-size:14px\">&nbsp;</span></p><p style=\"text-indent:28px\"><span style=\";font-family:Calibri;font-size:14px\"><span style=\"font-family:宋体\">首钢滑雪大跳台选址首钢老工业园区北区，大跳台周边老厂房和工业构筑物经过修缮改造，具备赛事配套服务功能，充分考虑首钢工业遗存的利用价值。北京冬奥会后，首钢滑雪大跳台成为世界首例永久性保留和使用的滑雪大跳台场馆，可承办国内外大跳台项目体育比赛，成为专业运动员和运动队训练场地、青少年后备人才选拔基地、赛事管理人员训练基地等，直接服务中国冰雪运动发展。同时，首钢滑雪大跳台成为向公众开放的北京冬奥会标志性景观地点和休闲健身活动场地，变身服务大众的体育主题公园。</span></span></p><p><img src=\"/media/Scenic/ueditor/首钢3_20220102165635_142.png\" title=\"\" alt=\"首钢3.png\"/></p>','spots/thumbnail/2022/01/首钢2.png','spots/mainfigure/2022/01/首钢滑雪大跳台.jpg','natural','0000000','全年','北京市石景山区群明湖西街',100,112.460033,34.624376,'2022-01-02 16:54:00.000000'),(7,'五棵松体育中心','<p style=\"text-indent:28px\"><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">五棵松体育中心，位于北京市西四环和长安街的交汇处五棵松桥东北角，</span> <span style=\"font-family:宋体\">五棵松体育中心为北京</span><span style=\"font-family:Calibri\">2022</span><span style=\"font-family:宋体\">年冬奥会冰上项目场馆。 &nbsp;</span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:宋体\">五棵松体育中心建筑面积</span><span style=\"font-family:Calibri\">6.3</span><span style=\"font-family:宋体\">万平方米，能容纳</span><span style=\"font-family:Calibri\">18000</span><span style=\"font-family:宋体\">人。</span><span style=\"font-family:Calibri\">2008</span><span style=\"font-family:宋体\">年夏季奥运会后，成为了一座文化体育活动的地标式场馆。 </span></span></p><p style=\"text-indent:28px\"><span style=\";font-family:宋体;font-size:14px\"><span style=\"font-family:Calibri\">2020</span><span style=\"font-family:宋体\">年</span><span style=\"font-family:Calibri\">12</span><span style=\"font-family:宋体\">月，北京</span><span style=\"font-family:Calibri\">2022</span><span style=\"font-family:宋体\">年冬奥会冰球比赛场馆五棵松体育中心改造工程完工。</span></span></p><p><img src=\"/media/Scenic/ueditor/五棵松1_20220102165850_956.png\" title=\"\" alt=\"五棵松1.png\"/></p>','spots/thumbnail/2022/01/五棵松体育中心.jpg','spots/mainfigure/2022/01/五棵松体育中心.jpg','natural','000000000','全年','北京市海淀区复兴路辅路',100,112.460033,34.624376,'2022-01-02 16:57:00.000000');
